    //         },
    //         body: JSON.stringify({
    //             // user_id: user.id,
    //             // first_name: first_name,
    //             // last_name: last_name,
    //             // email: email,
    //             // phone: phone,
    //             // type: type,
    //             // notes: notes
    //         })
    //     }

    //     try {
    //         const res = await fetch(url, options);
    //         const data = await res.json();
    //         if (data.status === 'ok') {
    //             // Show success msg
    //             console.log(data)
    //             getClients()
    //             setChecked(false)
    //             e.target.first_name.value = ''
    //             e.target.last_name.value = ''
    //             e.target.email.value = ''
