import React, { useContext, useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { UserContext } from '../Context/UserContext';

const BACK_END_URL = process.env.REACT_APP_BACKEND_URL

export default function SingleClient() {

    const { user } = useContext(UserContext)
    const { client_id } = useParams()
    const [client, setClient] = useState({})

    const getClient = async () => {
        const token = user.apitoken

        const res = await fetch(`${BACK_END_URL}/api/client/${client_id}`, {
            method: "GET",
            headers: {
                Authorization: `Bearer ${token}`
            },
        })
        const data = await res.json()
        console.log(data)
        setClient(data.client)
    };

    useEffect(() => { getClient() }, [])

    return (
        <div className='flex flex-col items-center'>
            <div className='flex justify-center mt-5'>
                <div className="tabs">
                    <a className="tab tab-lifted tab-lg ">Formulas</a>
                    <a className="tab tab-lifted tab-lg  tab-active">Info</a>
                    <a className="tab tab-lifted tab-lg ">Large</a>
                </div>
            </div>
            <div className="card prose w-96 bg-base-100 shadow-xl">
                <div className="card-body">
                    <h1 className="card-title">Card title!</h1>
                    <p>{client.first_name} {client.last_name}</p>
                </div>
            </div>
        </div>
    )
}
