import React, { useContext, useEffect, useState } from 'react'
import { UserContext } from '../Context/UserContext';
import { GlobalContext } from '../Context/GlobalContext';
import { getDatabase, ref, set, child, get } from "firebase/database";
import Client_List from '../Components/Client_List';

const BACK_END_URL = process.env.REACT_APP_BACKEND_URL

export default function Clients() {

    const { clients, setClients } = useContext(GlobalContext)
    const { user, setUser } = useContext(UserContext)

    const addClient = async (e) => {
        e.preventDefault()
        const token = user.apitoken

            const first_name = e.target.first_name.value
            const last_name = e.target.last_name.value
            const email = e.target.email.value
            const phone = e.target.password.value
            const type = e.target.type.value

            const url = BACK_END_URL + '/api/addclient';
            const options = {
                method: "POST",
                headers: {
                    "Content-Type": 'application/json',
                    Authorization: `Bearer ${token}`
                },
                body: JSON.stringify({
                    first_name: first_name,
                    last_name: last_name,
                    email: email,
                    phone: phone,
                    type: type
                })
            }

            try{
                const res = await fetch(url, options);
                const data = await res.json();
                if (data.status === 'ok') {
                    // Show success msg
                    console.log(data)
                }else {
                    return console.log(data.message)
                }
            }
            catch{
                console.log("Could not add client. Try again?")
            }
    };

    const getClients = async () => {
        const token = user.apitoken

        const res = await fetch(`${BACK_END_URL}/api/userclients`, {
            method: "GET",
            headers: {
                Authorization: `Bearer ${token}`
            },
        })
        const data = await res.json()
        console.log(data)
        setClients(data.clients)
    };

    useEffect(() => { getClients() }, [])

    const showClients = () => {
        return clients.map((client, index) => <Client_List key={index} client={client} />)
    };

    return (
        <div>

            <div className='flex justify-center sm:align-end'>
                {/* The button to open modal */}
                <label htmlFor="my-modal-4" className="btn rounded-b-full pb-5 mb-5">Add Client</label>
            </div>

            {/* Put this part before </body> tag */}
            <input type="checkbox" id="my-modal-4" className="modal-toggle" />
            <label htmlFor="my-modal-4" className="modal modal-bottom sm:modal-middle cursor-pointer">
                <label className="modal-box relative" htmlFor="">
                    <form onSubmit={(e) => {}}>
                        <div className='flex justify-between'>
                            <h2 className='text-2xl'>Add New Client</h2>
                        </div>
                        <div className="form-control">
                            <br></br>
                            <label className="input-group input-group-vertical">
                                <span>First Name</span>
                                <input type="text" placeholder="First Name" className="input input-bordered" />
                            </label>

                            <br></br>
                            <label className="input-group input-group-vertical">
                                <span>Last Name</span>
                                <input type="text" placeholder="Last Name" className="input input-bordered" />
                            </label>

                            <br></br>
                            <label className="input-group input-group-vertical">
                                <span>Email</span>
                                <input type="text" placeholder="info@site.com" className="input input-bordered" />
                            </label>

                            <br></br>
                            <label className="input-group input-group-vertical">
                                <span>Phone</span>
                                <input type="text" placeholder="4443331122" className="input input-bordered" />
                            </label>

                            <br></br>
                            <div className='flex justify-around'>
                                <div className="form-control">
                                    <label className="label cursor-pointer">
                                        <span className="label-text pr-1">Color</span>
                                        <input type="radio" name="type" value="color" className="radio checked:accent-content" checked />
                                    </label>
                                </div>
                                <div className="form-control">
                                    <label className="label cursor-pointer">
                                        <span className="label-text pr-1">Both</span>
                                        <input type="radio" name="type" value="cut & color" className="radio checked:accent-content" checked />
                                    </label>
                                </div>
                                <div className="form-control">
                                    <label className="label cursor-pointer">
                                        <span className="label-text pr-1">Cut</span>
                                        <input type="radio" name="type" value="cut" className="radio checked:accent-content" checked />
                                    </label>
                                </div>
                            </div>

                            <div className='flex justify-center mt-5'>
                                <button className="btn btn-wide form-control">Save</button>
                            </div>

                        </div>
                    </form>
                </label>
            </label>

            {/* Start of the client table */}
            <div className="overflow-x-auto w-full">
                <table className={`table table-compact  table-zebra w-full`}>
                    {/* head */}
                    <thead>
                        <tr>
                            <th>Client</th>
                            {window.innerWidth > 400 ? <th>Job</th> : ''}
                            <th >
                                <form className="form-control ">
                                    <div className="input-group">
                                        <input type="text" placeholder="Searchâ€¦" className="input input-bordered input-sm w-36" />
                                        <button className="btn btn-square btn-sm">
                                            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" /></svg>
                                        </button>
                                    </div>
                                </form>
                            </th>
                        </tr>
                    </thead>
                    <tbody>
                        {/* row 1 */}
                        {showClients()}

                    </tbody>
                    {/* foot */}
                    <tfoot>
                        <tr>
                            <th>Client</th>
                            {window.innerWidth > 400 ? <th>Job</th> : ''}
                            <th></th>
                        </tr>
                    </tfoot>

                </table>
            </div>

        </div>
    )
}
