import React, { useContext, useEffect, useState } from 'react'
import { Link, useNavigate, useParams } from 'react-router-dom'
import { UserContext } from '../Context/UserContext';
import { GlobalContext } from '../Context/GlobalContext';

import orange_cyberpunk from '../images/orange_cyberpunk.png'
import haircut_background from '../images/haircut_background.png'
import blue_hair from '../images/blue_hair.png'
import group from '../images/group.png'
import dual_tone from '../images/dual_tone.png'
import stunty_bob from '../images/stunty_bob.png'


const BACK_END_URL = process.env.REACT_APP_BACKEND_URL

export default function SingleClient() {

    const navigate = useNavigate()

    const { user } = useContext(UserContext)
    const { currentClient, setCurrentClient } = useContext(GlobalContext)
    const { client_id } = useParams()

    console.log(client_id)

    useEffect(() => { getClient() }, [])

    const getClient = async () => {
        const token = user.apitoken

        const res = await fetch(`${BACK_END_URL}/api/client/${client_id}`, {
            method: "GET",
            headers: {
                Authorization: `Bearer ${token}`
            },
        })
        const data = await res.json()
        console.log(data)
        setCurrentClient(data.client)
    };

    const deleteClient = async () => {
        const token = user.apitoken

        const res = await fetch(`${BACK_END_URL}/api/delete/client/${client_id}`, {
            method: "POST",
            headers: {
                Authorization: `Bearer ${token}`
            },
        })
        const data = await res.json()
        console.log(data)
        navigate('/clients')
    };

    // const editClient = async (e) => {
    //     e.preventDefault()
    //     const token = user.apitoken

    //     // const first_name = e.target.first_name.value
    //     // const last_name = e.target.last_name.value
    //     // const email = e.target.email.value
    //     // const phone = e.target.phone.value
    //     // const type = e.target.type.value


    //     const url = BACK_END_URL + '/api/addclient';
    //     const options = {
    //         method: "POST",
    //         headers: {
    //             "Content-Type": 'application/json',
    //             Authorization: `Bearer ${token}`
    //         },
    //         body: JSON.stringify({
    //             // user_id: user.id,
    //             // first_name: first_name,
    //             // last_name: last_name,
    //             // email: email,
    //             // phone: phone,
    //             // type: type,
    //             // notes: notes
    //         })
    //     }

    //     try {
    //         const res = await fetch(url, options);
    //         const data = await res.json();
    //         if (data.status === 'ok') {
    //             // Show success msg
    //             console.log(data)
    //             getClients()
    //             setChecked(false)
    //             e.target.first_name.value = ''
    //             e.target.last_name.value = ''
    //             e.target.email.value = ''
    //             e.target.phone.value = ''
    //             e.target.type.value = ''
    //             setNotes('')


    //         } else {
    //             return console.log(data.message)
    //         }
    //     }
    //     catch {
    //         console.log("Could not add client. Try again?")
    //     }
    // };

    return (
        <div className='flex flex-col items-center' style={{ backgroundImage: `url(${group})` }}>
            <h2 className="card-title mt-5 text-base-100 bg-primary h-10 pr-2 pl-2">{`${currentClient.first_name} ${currentClient.last_name}`}</h2>
            <div className='flex justify-center mt-5'>
                <div className="tabs">
                    <Link to={`/client/${client_id}/formulas`} className="tab tab-lifted tab-lg ">Formulas</Link>
                    <a className="tab tab-lifted tab-lg  tab-active">Info</a>
                    <Link to={`/client/${client_id}/colorchart`} className="tab tab-lifted tab-lg ">Color Chart</Link>
                </div>
            </div>
            <div className="card w-96 bg-base-100 shadow-xl">
                <div className="card-body">
                    <div className="form-control">
                        <label className="input-group input-group-vertical">
                            <span>Email</span>
                            <p className='border border-base-300 p-2'>{`${currentClient.email}`}</p>
                        </label>
                    </div>
                    <div className="form-control">
                        <label className="input-group input-group-vertical">
                            <span>Phone Number</span>
                            <p className='border border-base-300 p-2'>{`${currentClient.phone}`}</p>
                        </label>
                    </div>
                    <div className="form-control">
                        <label className="input-group input-group-vertical">
                            <span>Birthday</span>
                            <p className='border border-base-300 p-2'>{currentClient.birthday}</p>

                        </label>
                    </div>
                    <div className="form-control">
                        <label className="input-group input-group-vertical">
                            <span>Notes</span>
                            <p className='border border-base-300 p-2'>{currentClient.notes}</p>
                        </label>
                    </div>

                    <div className='flex justify-around mt-5'>
                        {/* The button to open modal */}
                        <label htmlFor="decision-modal" className="btn">Delete</label>
                        <button className="btn">Edit</button>
                    </div>


                    {/* Put this part before </body> tag */}
                    <input type="checkbox" id="decision-modal" className="modal-toggle" />
                    <div className="modal">
                        <div className="modal-box bg-warning">
                            <h3 className="font-bold text-lg">Remove Client</h3>
                            <p className="py-4">Are you sure you want to delete this client from your database? <b>This can't be undone!</b></p>
                            <div className="modal-action flex justify-around">
                                <label htmlFor="decision-modal" onClick={deleteClient} className="btn btn-error">I'm sure!</label>
                                <label htmlFor="decision-modal" className="btn">Nope! Nevermind!</label>
                            </div>
                        </div>

                    </div>

                </div>
            </div>
        </div>
    )
}
